import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

import Search from '../components/Search/Search';
import Loader from '../components/Loader/Loader';

class App extends Component {
	
	constructor(props) {
        
        super(props);
        
		this.state = {
			pokemon : null,
			name : null,
			number : 0,
			spriteUrl : null,
			types : [],
			height : 0,
			weight : 0,
			isPokemonShowed : false
		}
		
		this.handleChange = this.handleChange.bind(this);
        this.showInfo = this.showInfo.bind(this);
        
	}

	handleChange = (event) => {
		this.setState({pokemon: event.target.value});
	}
	
	showInfo = () => {

		axios.get('https://pokeapi.co/api/v2/pokemon/' + this.state.pokemon + '/').then(response => {

			const info = response.data;

			this.setState({
				name : info.name,
				number : info.id,
				spriteUrl : info.sprites.front_default,
				types : info.types,
				height : info.height / 10,
				weight : info.weight / 10,
				isPokemonShowed : true
			});
			

		}).catch(() => {
			this.setState({
				isPokemonShowed : false,
				message : "Pokemon not found."
			});
		});

	}

  	render() {
		return (
			<div className="App">
				<Search changed={this.handleChange} clicked={this.showInfo}/>
				<Loader class="hide"/>
			</div>
		);
  	}
}

export default App;
